    private static void addProvince(String itemID, String name, double area, String capital, String region, int population, double PCI, String description, int teleCode, int[] vehicleCode, int popDensity,
                                    ArrayList<subProvince> district, ArrayList<subProvince> city, ArrayList<subProvince> town )
    {
        // adding province to the HashMap
        Province currProv= new Province(itemID,name,area,capital,region,population, PCI, description, teleCode, vehicleCode, popDensity, district, city, town);
        provRef.put(itemID,currProv);
        listProvince.add(currProv);
        Log.d("add",currProv.toString());
        AreaComp.add(new Double(area));
        PopComp.add(new Integer(population));
        PopDensComp.add(new Integer(popDensity));
        PCIComp.add(new Double(PCI));
    }

    private static void addProvince(String itemID, String name, double area, String capital, String region, int population, double PCI, String description, int teleCode, int[] vehicleCode, int popDensity, boolean isCity,
                                    ArrayList<subProvince> district, ArrayList<subProvince> city, ArrayList<subProvince> town )
    {
        Province currProv= new Province(itemID,name,area,capital,region,population, PCI,description, teleCode, vehicleCode, popDensity,isCity, district, city, town);
        provRef.put(itemID,currProv);
        listProvince.add(currProv);

        AreaComp.add(new Double(area));
        PopComp.add(new Integer(population));
        PopDensComp.add(new Integer(popDensity));
        PCIComp.add(new Double(PCI));
    }
